# importando o numpy
import numpy as np
# criando arrays
lista = [1, 2, 3]
a = np.array(lista)
print(a)
type(a)

# criando arrays
a = np.array([1, 2, 3])
print(a)
type(a)

# criando arrays com tipos de dados diferentes
lista = [1, 2, "mentorama", 3, 4]
a = np.array(lista)
